        ///
        /// // Initialize state with a linear congruential random number generator algorithm.
        /// mpz_t a = new mpz_t();
        /// gmp_lib.mpz_init_set_ui(a, 100000U);
        /// gmp_lib.gmp_randinit_lc_2exp(state, a, 13, 300);
