        /// gmp_lib.mpq_clear(q)
        /// gmp_lib.mpf_clear(f)
        /// </code>
        /// </example>
        public static int gmp_asprintf(ptr<char_ptr> /*char ***/ pp, /*const*/ string /*char **/ fmt, params object[] /*...*/ args)
        {
            return gmp_vasprintf(pp, fmt, args);
        }

