        ///     Assert.IsTrue(gmp_lib.mpz_getlimbn(op, 0) = &amp;Habcd12347ab824fdUL)
        ///     Assert.IsTrue(gmp_lib.mpz_getlimbn(op, 1) = &amp;H1000)
        /// End If
