        /// Dim x2 As New mpz_t()
        /// Dim x3 As New mpz_t()
        ///
        /// ' Initialize the integers.
        /// gmp_lib.mpz_inits(x1, x2, x3, Nothing)
        ///
        /// ' Assert that their value is 0.
        /// Assert.IsTrue(gmp_lib.mpz_get_si(x1) = 0)
        /// Assert.IsTrue(gmp_lib.mpz_get_si(x2) = 0)
        /// Assert.IsTrue(gmp_lib.mpz_get_si(x3) = 0)
