        {
            public SafeHandle(IntPtr handle)
                : base(true)
            {
                SetHandle(handle);
            }

            public IntPtr Handle
            {
                get
                {
                    return handle;
                }
            }

            protected override bool ReleaseHandle()
            {
                NativeLibrary.Free(handle);
                return true;
            }
